import React, { useState } from 'react';

const BANK_CODES = {
  'Banco BBVA': '017',
  'Banco Santander': '072',
  'Banco Galicia': '007',
  'Banco Nacion': '011',
  'Banco Macro': '285',
  'Banco Comafi': '299',
  'Banco ICBC': '150'
};

const INTEREST_RATES = {
  3: 0.15,
  6: 0.25,
  9: 0.35,
  12: 0.45
};

const OfferApproval = ({ formData, onAccept }) => {
  const [amount, setAmount] = useState(300000);
  const [installments, setInstallments] = useState(3);
  const [cbu, setCbu] = useState('');
  const [acceptTerms, setAcceptTerms] = useState(false);
  const [errors, setErrors] = useState({});

  const calculateMonthlyPayment = () => {
    const rate = INTEREST_RATES[installments];
    const totalAmount = amount * (1 + rate);
    return (totalAmount / installments).toFixed(2);
  };

  const validateCbu = () => {
    if (cbu.length !== 22) {
      return 'El CBU debe tener exactamente 22 dígitos';
    }

    const bankCode = cbu.substring(0, 3);
    const expectedCode = BANK_CODES[formData.banco];

    if (bankCode !== expectedCode) {
      return 'El CBU no corresponde al banco seleccionado';
    }

    return null;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const cbuError = validateCbu();
    if (cbuError) {
      setErrors({ cbu: cbuError });
      return;
    }

    if (!acceptTerms) {
      setErrors({ terms: 'Debe aceptar los términos y condiciones' });
      return;
    }

    setErrors({});
    onAccept({
      amount,
      installments,
      monthlyPayment: calculateMonthlyPayment(),
      cbu,
      interestRate: INTEREST_RATES[installments]
    });
  };

  return (
    <div className="offer-approval">
      <div className="approval-message">
        <h2>¡Su oferta fue aprobada!</h2>
        <h3>Hola {formData.nombre}</h3>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="offer-details">
          <h3>Detalles de la Oferta</h3>

          <label>
            Monto del crédito (máximo $300.000):
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(Math.min(300000, e.target.value))}
              max="300000"
              min="1000"
              step="1000"
            />
          </label>

          <label>
            Cantidad de cuotas:
            <select
              value={installments}
              onChange={(e) => setInstallments(parseInt(e.target.value))}
            >
              <option value={3}>3 cuotas (15% interés)</option>
              <option value={6}>6 cuotas (25% interés)</option>
              <option value={9}>9 cuotas (35% interés)</option>
              <option value={12}>12 cuotas (45% interés)</option>
            </select>
          </label>

          <div className="payment-summary">
            <p><strong>Cuota mensual: ${calculateMonthlyPayment()}</strong></p>
            <p>Tasa de interés: {(INTEREST_RATES[installments] * 100)}%</p>
          </div>
        </div>

        <div className="cbu-section">
          <label>
            CBU (22 dígitos):
            <input
              type="text"
              value={cbu}
              onChange={(e) => setCbu(e.target.value.replace(/\D/g, '').slice(0, 22))}
              placeholder="Ingrese su CBU de 22 dígitos"
              maxLength="22"
            />
          </label>
          {errors.cbu && <span className="error">{errors.cbu}</span>}
        </div>

        <div className="terms-section">
          <label>
            <input
              type="checkbox"
              checked={acceptTerms}
              onChange={(e) => setAcceptTerms(e.target.checked)}
            />
            Acepto los términos y condiciones
          </label>
          {errors.terms && <span className="error">{errors.terms}</span>}
        </div>

        <button type="submit">Aceptar Oferta</button>
      </form>
    </div>
  );
};

export default OfferApproval;
