import React, { useState } from 'react';

const BANKS = [
  'Banco BBVA',
  'Banco Santander', 
  'Banco Galicia',
  'Banco Nacion',
  'Banco Macro',
  'Banco Comafi',
  'Banco ICBC'
];

const ENABLED_BANKS = [
  'Banco BBVA',
  'Banco Santander',
  'Banco Galicia', 
  'Banco Nacion'
];

const CreditForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    dni: '',
    telefono: '',
    email: '',
    banco: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!ENABLED_BANKS.includes(formData.banco)) {
      alert('Lo sentimos, su banco no está habilitado para otorgar créditos.');
      return;
    }

    onSubmit(formData);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <form onSubmit={handleSubmit} className="credit-form">
      <h2>Datos Personales</h2>

      <input
        type="text"
        name="nombre"
        placeholder="Nombre"
        value={formData.nombre}
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="apellido"
        placeholder="Apellido"
        value={formData.apellido}
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="dni"
        placeholder="DNI"
        value={formData.dni}
        onChange={handleChange}
        required
      />

      <input
        type="tel"
        name="telefono"
        placeholder="Teléfono"
        value={formData.telefono}
        onChange={handleChange}
        required
      />

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
      />

      <select
        name="banco"
        value={formData.banco}
        onChange={handleChange}
        required
      >
        <option value="">Seleccione su banco</option>
        {BANKS.map(bank => (
          <option key={bank} value={bank}>{bank}</option>
        ))}
      </select>

      <button type="submit">Continuar</button>
    </form>
  );
};

export default CreditForm;
