import React, { useState } from 'react';

const DocumentValidation = ({ formData, offer }) => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [error, setError] = useState('');

  // Simulación de validación de documento
  const validateDocument = async () => {
    // Simula AWS Rekognition
    return new Promise(resolve => {
      setTimeout(() => resolve(true), 1000);
    });
  };

  // Simulación de SMS
  const sendSMSNotification = async () => {
    console.log(`SMS enviado a ${formData.telefono}: Crédito de $${offer.amount} aprobado`);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type.startsWith('image/')) {
      setFile(selectedFile);
      setError('');
    } else {
      setError('Por favor seleccione una imagen válida');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Por favor seleccione un archivo');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const isValidDocument = await validateDocument();

      if (!isValidDocument) {
        setError('El archivo no parece ser un documento válido. Por favor intente nuevamente.');
        setLoading(false);
        return;
      }

      await sendSMSNotification();
      setCompleted(true);
      setLoading(false);
    } catch (error) {
      setError('Error al procesar el documento. Intente nuevamente.');
      setLoading(false);
    }
  };

  if (completed) {
    return (
      <div className="completion-message">
        <h2>¡Transacción Aprobada!</h2>
        <p>Su crédito de <strong>${offer.amount}</strong> ha sido aprobado y transferido exitosamente.</p>
        <p>SMS enviado a: {formData.telefono}</p>
        <div className="offer-summary">
          <h3>Resumen de su crédito:</h3>
          <p>Monto: ${offer.amount}</p>
          <p>Cuotas: {offer.installments}</p>
          <p>Cuota mensual: ${offer.monthlyPayment}</p>
          <p>Tasa de interés: {(offer.interestRate * 100)}%</p>
        </div>
      </div>
    );
  }

  return (
    <div className="document-validation">
      <h2>Validación de Documento</h2>
      <p>Por favor, suba una foto de su documento de identidad para completar la solicitud.</p>

      <form onSubmit={handleSubmit}>
        <div className="file-upload">
          <label>
            Seleccionar documento:
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              disabled={loading}
            />
          </label>

          {file && (
            <div className="file-preview">
              <p>Archivo seleccionado: {file.name}</p>
              <img 
                src={URL.createObjectURL(file)} 
                alt="Preview" 
                style={{ maxWidth: '200px', maxHeight: '200px' }}
              />
            </div>
          )}
        </div>

        {error && <div className="error">{error}</div>}

        <button type="submit" disabled={!file || loading}>
          {loading ? 'Validando documento...' : 'Validar Documento'}
        </button>
      </form>
    </div>
  );
};

export default DocumentValidation;
