import React, { useState } from 'react';
import CreditForm from './components/CreditForm';
import OfferApproval from './components/OfferApproval';
import DocumentValidation from './components/DocumentValidation';
import './App.css';

const App = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});
  const [offer, setOffer] = useState({});

  const nextStep = () => setStep(step + 1);

  return (
    <div className="app">
      <h1>Solicitud de Cr√©dito</h1>
      {step === 1 && (
        <CreditForm 
          onSubmit={(data) => {
            setFormData(data);
            nextStep();
          }}
        />
      )}
      {step === 2 && (
        <OfferApproval 
          formData={formData}
          onAccept={(offerData) => {
            setOffer(offerData);
            nextStep();
          }}
        />
      )}
      {step === 3 && (
        <DocumentValidation 
          formData={formData}
          offer={offer}
        />
      )}
    </div>
  );
};

export default App;
